import React, { useState } from "react";




function Modify(){

    const [title , setTitle] = useState("");
    const [text , setText] = useState("");
    const [content , setContent] = useState("");
    // const [writer , setWriter] = useState("");


    const onChange = (e) => {
        const {target} = e;
        if(target.id === title) setTitle(target.value);
        else if(target.id === text ) setText(target.value);
        else if(target.id === content) setContent(target.value);
    }

const onSubmit = (e) => {
    e.preventDefault();
}

    return(
        <form onSubmit={onSubmit}>
            <input type="text" id={title} value={title} onChange={onChange} placeholder="title"></input> <br></br>
            <input type="text" id={text} value={text} onChange={onChange} placeholder="name"></input> <br></br>
            <textarea type="textarea" id={content} value={content} onChange={onChange} placeholder="content"></textarea>
            <input type="submit" value ="submit"></input>
        </form>
    );
}

export default Modify;
